import React from 'react';
import {
  ChakraProvider as BaseChakraProvider,
  ChakraProviderProps,
} from '@chakra-ui/provider';
import { theme as defaultTheme } from '@fidesui/react-theme';

export function FidesProvider({
  children,
  theme = defaultTheme,
}: ChakraProviderProps) {
  return <BaseChakraProvider theme={theme}>{children}</BaseChakraProvider>;
}

export type { ChakraProviderProps as FidesProviderProps } from '@chakra-ui/provider';
